var body=document.body,nav=document.getElementsByTagName("nav")[0],button=document.getElementById("dark-mode-toggle"),icon=document.getElementById("dark-mode-toggle-icon"),savedTheme=localStorage.getItem("dark-mode-storage")||"light",isDark=savedTheme=="dark";setTheme(savedTheme),button.addEventListener("click",()=>{nav.classList.add("transition"),body.classList.add("transition"),isDark=!isDark,isDark?setTheme("dark"):setTheme("light")});function setTheme(a){localStorage.setItem("dark-mode-storage",a),a==="dark"?(body.classList.add("darktheme"),icon.className="fas fa-sun"):a==="light"&&(body.classList.remove("darktheme"),icon.className="fas fa-moon")}