const body=document.body,navHamburger=document.getElementById("navbar-hamburger"),navMenu=document.getElementById("navbar-menu");navHamburger.addEventListener("click",()=>{body.classList.toggle("disable-scroll"),navHamburger.classList.toggle("is-active"),navMenu.classList.toggle("is-active")});let resizeTimer;window.addEventListener("resize",()=>{navMenu.classList.add("no-transitions"),clearTimeout(resizeTimer),resizeTimer=setTimeout(()=>{navMenu.classList.remove("no-transitions")},400)})