const scrollButton=document.getElementById("scroll-to-top");document.addEventListener("scroll",handleScroll);function handleScroll(){var a=document.documentElement.scrollHeight-document.documentElement.clientHeight,b=.5;document.documentElement.scrollTop/a>b?scrollButton.classList.contains("show-scroll-to-top")||scrollButton.classList.add("show-scroll-to-top"):scrollButton.classList.contains("show-scroll-to-top")&&scrollButton.classList.remove("show-scroll-to-top")}scrollButton.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})})