document.addEventListener("scroll",handleScroll);var button=document.getElementById("scroll-to-top");function handleScroll(){var a=document.documentElement.scrollHeight-document.documentElement.clientHeight,b=.5;document.documentElement.scrollTop/a>b?button.classList.contains("show-scroll-to-top")||button.classList.add("show-scroll-to-top"):button.classList.contains("show-scroll-to-top")&&button.classList.remove("show-scroll-to-top")}button.addEventListener("click",scrollToTop);function scrollToTop(){window.scrollTo({top:0,behavior:"smooth"})}