const scrollButton=document.getElementById("scroll-to-top");document.addEventListener("scroll",handleScroll);function handleScroll(){var e=document.documentElement.scrollHeight-document.documentElement.clientHeight,t=.1618;document.documentElement.scrollTop/e>t?scrollButton.classList.contains("show-scroll-to-top")||scrollButton.classList.add("show-scroll-to-top"):scrollButton.classList.contains("show-scroll-to-top")&&scrollButton.classList.remove("show-scroll-to-top")}scrollButton.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})})